import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Navbar = ({ auth }) => {
  const location = useLocation();

  // Only show the main navigation on certain paths
  const hideNav = ['/', '/login', '/register'].includes(location.pathname);

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/">FitnessTracker</Link>
      </div>

      {!hideNav && auth.isAuthenticated ? (
        <div className="navbar-nav">
          <Link to="/dashboard" className="nav-item">
            Dashboard
          </Link>
          <Link to="/exercise" className="nav-item">
            Exercise
          </Link>
          <Link to="/diet" className="nav-item">
            Diet
          </Link>
          <Link to="/goals" className="nav-item">
            Goals
          </Link>
          <Link to="/groups" className="nav-item">
            Groups
          </Link>
          <button onClick={auth.logout} className="nav-item logout-btn">
            Logout
          </button>
        </div>
      ) : (
        <div className="navbar-nav">
          {location.pathname !== '/login' && 
            <Link to="/login" className="nav-item">Login</Link>
          }
          {location.pathname !== '/register' && 
            <Link to="/register" className="nav-item">Register</Link>
          }
        </div>
      )}
    </nav>
  );
};

export default Navbar;
